   protected function addFiltersCriteria($c)
  {
    if (isset($this->filters['codrec_is_empty']))
    {
      $criterion = $c->getNewCriterion(CarecaudPeer::CODREC, '');
      $criterion->addOr($c->getNewCriterion(CarecaudPeer::CODREC, null, Criteria::ISNULL));
      $c->add($criterion);
    }
    else if (isset($this->filters['codrec']) && $this->filters['codrec'] !== '')
    {
      $c->add(CarecaudPeer::CODREC, '%'.strtr($this->filters['codrec'], '*', '%').'%', Criteria::LIKE);
    $c->setIgnoreCase(true);
    }
    if (isset($this->filters['codtiprec_is_empty']))
    {
      $criterion = $c->getNewCriterion(CarecaudPeer::CODTIPREC, '');
      $criterion->addOr($c->getNewCriterion(CarecaudPeer::CODTIPREC, null, Criteria::ISNULL));
      $c->add($criterion);
    }
    else if (isset($this->filters['codtiprec']) && $this->filters['codtiprec'] !== '')
    {
      $c->add(CarecaudPeer::CODTIPREC, '%'.strtr($this->filters['codtiprec'], '*', '%').'%', Criteria::LIKE);
    $c->setIgnoreCase(true);
    }
  }
  protected function addSortCriteria($c)
  {
    if ($sort_column = $this->getUser()->getAttribute('sort', null, 'sf_admin/carecaud/sort'))
    {
      $sort_column = CarecaudPeer::translateFieldName($sort_column, BasePeer::TYPE_FIELDNAME, BasePeer::TYPE_COLNAME);
      if ($this->getUser()->getAttribute('type', null, 'sf_admin/carecaud/sort') == 'asc')
      {
        $c->addAscendingOrderByColumn($sort_column);
      }
      else
      {
        $c->addDescendingOrderByColumn($sort_column);
      }
    }
  }

