   protected function addFiltersCriteria($c)
  {
    if (isset($this->filters['codprofes_is_empty']))
    {
      $criterion = $c->getNewCriterion(NpprofesionPeer::CODPROFES, '');
      $criterion->addOr($c->getNewCriterion(NpprofesionPeer::CODPROFES, null, Criteria::ISNULL));
      $c->add($criterion);
    }
    else if (isset($this->filters['codprofes']) && $this->filters['codprofes'] !== '')
    {
      $c->add(NpprofesionPeer::CODPROFES, '%'.strtr($this->filters['codprofes'], '*', '%').'%', Criteria::LIKE);
    $c->setIgnoreCase(true);
    }
    if (isset($this->filters['desprofes_is_empty']))
    {
      $criterion = $c->getNewCriterion(NpprofesionPeer::DESPROFES, '');
      $criterion->addOr($c->getNewCriterion(NpprofesionPeer::DESPROFES, null, Criteria::ISNULL));
      $c->add($criterion);
    }
    else if (isset($this->filters['desprofes']) && $this->filters['desprofes'] !== '')
    {
      $c->add(NpprofesionPeer::DESPROFES, '%'.strtr($this->filters['desprofes'], '*', '%').'%', Criteria::LIKE);
    $c->setIgnoreCase(true);
    }
  }
  protected function addSortCriteria($c)
  {
    if ($sort_column = $this->getUser()->getAttribute('sort', null, 'sf_admin/npprofesion/sort'))
    {
      $sort_column = NpprofesionPeer::translateFieldName($sort_column, BasePeer::TYPE_FIELDNAME, BasePeer::TYPE_COLNAME);
      if ($this->getUser()->getAttribute('type', null, 'sf_admin/npprofesion/sort') == 'asc')
      {
        $c->addAscendingOrderByColumn($sort_column);
      }
      else
      {
        $c->addDescendingOrderByColumn($sort_column);
      }
    }
  }

